mapper.select:
select m.id,p.id as project_id,p.proj_name,m.member_start_time, TIMESTAMPDIFF(DAY,m.member_start_time,m.member_end_time) as last_time,m.member_application_status as apply_status, e.employee_name as member_recommend_name,DATE_FORMAT(m.create_time,'%Y-%m-%d %H:%i:%S') as create_time from member_apply as m left join project as p on m.fk_project_id=p.id left join employee as e on m.member_recommend_gh=e.employee_gh
order by FIELD(m.member_application_status,"待审批") DESC,m.create_time DESC
order by m.member_start_time ${startTimeSort}
order by m.member_application_status ${statusSort}

select s.skill_name from member_apply_skill as mas left join member_apply as ma on
ma.id=mas.member_apply_id left join skill as s on
s.id=mas.apply_skill_id where ma.id=#{id}

select concat(st.skill_test_name,st.skill_test_lv) as skill_test_name from member_apply_skill_test as mast left join member_apply as ma on
ma.id=mast.member_apply_id left join skill_test as st on
st.skill_test_id=mast.apply_skill_test_id where ma.id = #{id}

select id from member_apply where fk_project_id=#{projId} and fk_employee_gh=#{employeeGh} and

    member_recommend_gh=#{memberRecommendGh}


    member_recommend_gh is null

and member_join_sup=#{memberJoinSup} and member_join_type=#{memberJoinType} and member_start_time=#{memberStartTime}

        select m.id,p.id as project_id,p.proj_name,m.member_start_time,
        TIMESTAMPDIFF(DAY,m.member_start_time,m.member_end_time) as last_time,m.member_application_status as apply_status,
        e.employee_name as member_recommend_name,DATE_FORMAT(m.create_time,'%Y-%m-%d %H:%i:%S') as create_time from member_apply as m left join project as p on m.fk_project_id=p.id
		left join employee as e on m.member_recommend_gh=e.employee_gh left join employee as e1 on e1.employee_gh=m.fk_employee_gh where e1.employee_gh=#{employeeGh}

            order by FIELD(m.member_application_status,"待审批") DESC,m.create_time DESC


            order by m.member_start_time ${startTimeSort}


            order by m.member_application_status ${statusSort}

select distinct ma.id,e1.employee_gh,e1.employee_name,p.proj_name as applicant_proj_name,p.proj_place as applicant_proj_place,e3.employee_name as applicant_proj_pm,ma.member_join_type,ma.member_start_time,ma.member_end_time, ma.member_application_status,e2.employee_name as apply_approval_name,ma.applicant_is_agree from member_apply_agree as maa left join member_apply as ma on ma.id=maa.member_apply_id left join project as p on ma.fk_project_id=p.id and ma.fk_employee_gh=p.proj_PM_gh left join employee as e1 on ma.distribution_gh=e1.employee_gh left join employee as e2 on ma.apply_approval_gh=e2.employee_gh left join employee as e3 on e3.employee_gh=p.proj_PM_gh where maa.fk_employee_gh=#{employeeGh} and maa.agree_type=1 and ma.member_application_status="审批通过"
order by FIELD(ma.applicant_is_agree,0) DESC,maa.create_time DESC
order by ma.applicant_is_agree ${sort}

select distinct ma.id,e1.employee_gh,e1.employee_name,p.proj_name as applicant_proj_name,p.proj_place as applicant_proj_place,e3.employee_name as applicant_proj_pm,ma.member_join_type,ma.member_start_time,ma.member_end_time, ma.member_application_status,e2.employee_name as apply_approval_name,maa.is_agree as owner_is_agree from member_apply_agree as maa left join member_apply as ma on ma.id=maa.member_apply_id left join project as p on ma.fk_project_id=p.id and ma.fk_employee_gh=p.proj_PM_gh left join employee as e1 on ma.distribution_gh=e1.employee_gh left join employee as e2 on ma.apply_approval_gh=e2.employee_gh left join employee as e3 on e3.employee_gh=p.proj_PM_gh where maa.fk_employee_gh=#{employeeGh} and maa.agree_type=2 and ma.member_application_status="审批通过"
order by FIELD(maa.is_agree,0) DESC,maa.create_time DESC
order by maa.is_agree ${sort}
