mapper.select:
select m.id,p.id as project_id,p.proj_name,e.employee_name as leave_apply_name,e2.employee_gh,e2.employee_name as member_leave_name,m.member_leave_time, m.member_leave_status as leave_status,m.member_skill_evaluation,m.member_leave_remark,p1.proj_name as distribution_proj_name, DATE_FORMAT(m.create_time,'%Y-%m-%d %H:%i:%S') as create_time,e3.employee_name as leave_approval_name from member_leave as m left join project as p on m.fk_project_id=p.id left join employee as e on m.fk_employee_gh=e.employee_gh left join employee as e2 on m.member_leave_gh=e2.employee_gh left join employee as e3 on m.leave_approval_gh=e3.employee_gh left join project as p1 on p1.id=m.distribution_proj
order by FIELD(m.member_leave_status,"待审批") DESC,m.create_time DESC
order by m.member_leave_status ${sort}

select m.id,p.id as project_id,p.proj_name,e.employee_name as leave_apply_name,e2.employee_gh,e2.employee_name as member_leave_name,m.member_leave_time, m.member_leave_status as leave_status,m.member_skill_evaluation,m.member_leave_remark,p1.proj_name as distribution_proj_name, DATE_FORMAT(m.create_time,'%Y-%m-%d %H:%i:%S') as create_time,e3.employee_name as leave_approval_name from member_leave as m left join project as p on m.fk_project_id=p.id left join employee as e on m.fk_employee_gh=e.employee_gh left join employee as e2 on m.member_leave_gh=e2.employee_gh left join employee as e3 on m.leave_approval_gh=e3.employee_gh left join project as p1 on p1.id=m.distribution_proj where m.member_leave_status!="PMO调离" and e.employee_gh=#{employeeGh}
order by FIELD(m.member_leave_status,"待审批") DESC,m.create_time DESC
order by m.create_time ${sort}

mapper.update:
update member_leave set leave_approval_gh=#{leaveApprovalGh}, member_leave_status=#{memberLeaveStatus},
distribution_proj=#{projectId},
update_time=DATE_FORMAT(now(),'%Y-%m-%d %H:%i:%S') where id=#{id}
